import XCTest
@testable import Hardware
import StripeTerminal

final class ReceiptRendererTest: XCTestCase {
    func test_TextWithoutHtml() {
        let content = generateReceiptContent()

        let renderer = ReceiptRenderer(content: content)

        XCTAssertEqual(
            renderer.htmlContent().replacingOccurrences(of: " ", with: ""),
            expectedResultWithoutEscaping.replacingOccurrences(of: " ", with: "")
        )
    }
}

private extension ReceiptRendererTest {
    func generateReceiptContent() -> ReceiptContent {
        ReceiptContent(
                parameters: CardPresentReceiptParameters(
                        amount: 1,
                        formattedAmount: "1",
                        currency: "USD",
                        date: .init(),
                        storeName: "Test Store",
                        cardDetails: .init(
                                last4: "1234",
                                expMonth: 12,
                                expYear: 26,
                                cardholderName: "John Smith",
                                brand: .masterCard,
                                fingerprint: "fpr*****",
                                generatedCard: "pm_******",
                                receipt: .init(
                                        applicationPreferredName: "Stripe Credit",
                                        dedicatedFileName: "A00000000000000",
                                        authorizationResponseCode: "0000",
                                        applicationCryptogram: "XXXXXXXXXXXX",
                                        terminalVerificationResults: "101010101010101010",
                                        transactionStatusInformation: "6800",
                                        accountType: "credit"
                                ),
                                emvAuthData: "AD*******"),
                        orderID: 9201
                ),
                lineItems: [ReceiptLineItem(title: "Sample product #1", quantity: "2", amount: "25")],
                cartTotals: [ReceiptTotalLine(description: "description", amount: "13")],
                orderNote: nil
        )
    }
}

